    "sanskritName": "Urdhva Prasarita Eka Padasana",
    "imageUrl": "https://firebasestorage.googleapis.com/v0/b/prophet-data.appspot.com/o/yogaPoses%2FSplitsStanding_L.png?alt=media&token=bdbec6d0-cb1e-4d9b-82c5-c15376ceeac0",
    "difficulty": "Intermediate",
